import { createSlice } from "@reduxjs/toolkit";

export const authSlice = createSlice({
    name: 'auth',
    initialState: {
        user: JSON.parse(window.localStorage.getItem('user')) || null
    },
    reducers: {
        setUser: (state, action) => {
            state.user = action.payload;
            window.localStorage.setItem('user', JSON.stringify(action.payload)); // Side effect (could be moved outside reducer)
        },
        removeUser: (state) => {
            state.user = null;
            window.localStorage.removeItem('user'); // Side effect (could be moved outside reducer)
        },
        loadUser: (state) => {
            state.user = JSON.parse(window.localStorage.getItem('user')) || null;
        }
    }
});

export const { setUser, removeUser, loadUser } = authSlice.actions;

export default authSlice.reducer;
