// IntakeList.js
import React, { useState, useEffect } from 'react';
import IntakeEdit from './IntakeEdit';

function ListIntake(){
  const [intakeData, setIntakeData] = useState({});
  const [pageNumber, setPageNumber] = useState(0);
  const [editMode, setEditMode] = useState(false);
  const [selectedDate, setSelectedDate] = useState('');
  const user = JSON.parse(localStorage.getItem('loggedInUser'));

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem('intakeData'));
    if (data && data[user.username]) {
      setIntakeData(data[user.username]);
    }
  }, []);

  const pages = Math.ceil(Object.keys(intakeData).length / 10);
  const startIndex = pageNumber * 10;
  const endIndex = startIndex + 10;
  const paginatedData = Object.keys(intakeData).slice(startIndex, endIndex).map((date) => intakeData[date]);

  const handleEdit = (date) => {
    setEditMode(true);
    setSelectedDate(date);
  };

  const handleDelete = (date) => {
    const data = JSON.parse(localStorage.getItem('intakeData'));
    if (data && data[user.username] && data[user.username][date]) {
      delete data[user.username][date];
      localStorage.setItem('intakeData', JSON.stringify(data));
      setIntakeData(data[user.username]);
      alert('Intake deleted successfully');
    } else {
      alert('No intake data found for the selected date');
    }
  };

  return (
    <div>
      <h2>Intake List</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Intake (ml)</th>
            <th>Time</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {paginatedData.map((data) => (
            <tr key={data.date}>
              <td>{data.date}</td>
              <td>{data.intake}</td>
              <td>{data.time}</td>
              <td>
                <button onClick={() => handleEdit(data.date)}>Edit</button>
                <button onClick={() => handleDelete(data.date)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {editMode && (
        <IntakeEdit
          date={selectedDate}
          intakeData={intakeData}
          setIntakeData={setIntakeData}
          setEditMode={setEditMode}
        />
      )}
      <div>
        {pageNumber > 0 && (
          <button onClick={() => setPageNumber(pageNumber - 1)}>Previous</button>
        )}
        {pageNumber < pages - 1 && (
          <button onClick={() => setPageNumber(pageNumber + 1)}>Next</button>
        )}
      </div>
    </div>
  );
};

export default ListIntake;
