import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../Navbar";
function Login(){
const [data, setdata]= useState({email:'',password:''})
const navigate=useNavigate() 

const handleChange=(e)=>{
    setdata({...data,[e.target.name]:e.target.value})
}
const handleSubmit=(e)=>{
    e.preventDefault();
    if (!data.email && !data.password){
        alert('All Fields Are Required');
        return
    }
    const storedUsers=JSON.parse(localStorage.getitem("users")) || []
    const user=storedUsers.find((u)=>{u.email === data.email && u.password === data.password});
    if(user){
        alert('Login Successful')
        navigate('/')
    } else {
        alert('Invalid Username and Password');
        navigate('/register')
    }
}

return (
    <div>
      <Navbar />
      <div className="row p-5 justify-content-center">
        <div className="col-md-6">
          <h2 className="font-bold mb-4 text-center">Login</h2>
          <form onSubmit={handleSubmit} className="form-group">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={data.email}
              onChange={handleChange}
              className="border p-2 rounded form-control"
              required
            />
            <br />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={data.password}
              onChange={handleChange}
              className="border p-2 rounded form-control"
              required
            />
            <br />
            <button type="submit" className="btn bg-primary text-white p-2 rounded w-100">
              Login
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
export default Login;
