import React, { useState, useEffect } from 'react';
import Navbar from '../Navbar';
import { Link } from 'react-router-dom';
function ListIntake() {
  const [intakeData, setIntakeData] = useState({});
  const [pageNumber, setPageNumber] = useState(0);
  const user = JSON.parse(localStorage.getItem('loggedInUser')) || { username: '' };


  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('intakeData')) || {};
    setIntakeData(storedData[user.username] || {});
  }, [user.username]);

  const handleDelete = (date) => {
    const updatedData = { ...intakeData };
    delete updatedData[date];
    localStorage.setItem('intakeData', JSON.stringify({ [user.username]: updatedData }));
    setIntakeData(updatedData);
    alert('Intake deleted successfully');
  };

  const entries = Object.entries(intakeData);
  const pages = Math.ceil(entries.length / 10);
  const paginatedData = entries.slice(pageNumber * 10, (pageNumber + 1) * 10);

  return (
    <div>
        <Navbar/>
      <h2>Intake List</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Intake (ml)</th>
            <th>Time</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {paginatedData.map(([date, { intake, time }]) => (
            <tr key={date}>
              <td>{date}</td>
              <td>{intake}</td>
              <td>{time}</td>
              <td>
                <button onClick={() => alert('Edit functionality here')}>Edit</button>
                <button onClick={() => handleDelete(date)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        {pageNumber > 0 && <button onClick={() => setPageNumber(pageNumber - 1)}>Previous</button>}
        {pageNumber < pages - 1 && <button onClick={() => setPageNumber(pageNumber + 1)}>Next</button>}
      </div>
      <Link to="/listintake"> Intake</Link>
       <Link to="/compareintake">Comparison</Link>
    </div>
  );
}

export default ListIntake;
