import React, { useState, useEffect } from "react";
import Pagination from "./components/Pagination";

function ListIntake() {
  const [intakes, setIntakes] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [intakesPerPage] = useState(1);

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem("waterIntakeData")) || [];
    setIntakes(storedData);
  }, []);

  const handleDelete = (id) => {
    const filteredIntakes = intakes.filter((intake) => intake.id !== id);
    localStorage.setItem("waterIntakeData", JSON.stringify(filteredIntakes));
    setIntakes(filteredIntakes);
  };

  const handleEdit = (id, newIntake) => {
    const updatedIntakes = intakes.map((intake) => {
      if (intake.id === id) {
        return { ...intake, intake: newIntake };
      }
      return intake;
    });
    localStorage.setItem("waterIntakeData", JSON.stringify(updatedIntakes));
    setIntakes(updatedIntakes);
  };

  const indexOfLastIntake = currentPage * intakesPerPage;
  const indexOfFirstIntake = indexOfLastIntake - intakesPerPage;
  const currentIntakes = intakes.slice(indexOfFirstIntake, indexOfLastIntake);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div>
      <h2>Water Intake List</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Intake (ml)</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentIntakes.map((intake, index) => (
            <tr key={index}>
              <td>{intake.date}</td>
              <td>{intake.intake}</td>
              <td>
                <button onClick={() => handleDelete(intake.id)}>Delete</button>
                <button onClick={() => handleEdit(intake.id, prompt("Enter new intake"))}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Pagination
        intakesPerPage={intakesPerPage}
        totalIntakes={intakes.length}
        paginate={paginate}
      />
    </div>
  );
}

export default ListIntake;
