import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

function AddIntake() {
  const [waterIntake, setWaterIntake] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [storedData, setStoredData] = useState({});
 const navigate = useNavigate()
  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem("waterIntakeData")) || {};
    setStoredData(storedData);
  }, []);

  useEffect(() => {
    const today = new Date().toISOString().split("T")[0];
    if (storedData.date === today) {
      setError("You have already logged your water intake for today.");
    }
  }, [storedData]);

  const handleAddIntake = () => {
    if (!waterIntake) {
      setError("Please enter a water intake amount.");
      return;
    }

    const today = new Date().toISOString().split("T")[0];
    if (storedData.date === today) {
      setError("You have already logged your water intake for today.");
      setSuccess("")
      return;
    }

    const newStoredData = { date: today, intake: waterIntake };
    localStorage.setItem("waterIntakeData", JSON.stringify(newStoredData));
    setStoredData(newStoredData);

    setSuccess("Water intake logged successfully!");
    setError("");
    navigate('/listintake')
  };

  return (
    <div>
      <h2>Water Intake Tracker</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}
      <input
        type="number"
        value={waterIntake}
        onChange={(e) => setWaterIntake(e.target.value)}
        placeholder="Enter water intake (ml)"
      />
      <button onClick={handleAddIntake}>Add Intake</button>
      <Link to={"/listintake"}>List Intake</Link>
    </div>
  );
};

export default AddIntake;
