import React, { useState, useEffect } from "react";
import Navbar from "../Navbar";
import { useNavigate } from "react-router-dom";
const Register = () => {
  const [user, setUser] = useState({ name: "", email: "", password: "", passwordconf: "" });
  const [users, setUsers] = useState([]);
  const navigate =useNavigate()
  useEffect(() => {
    const storedUsers = JSON.parse(localStorage.getItem("users")) || [];
    setUsers(storedUsers);
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!user.name || !user.email || !user.password || !user.passwordconf) {
      alert("All fields are required!");
      return;
    }

    if (user.password !== user.passwordconf) {
      alert("Passwords do not match!");
      return;
    }

    const updatedUsers = [...users, user];
    setUsers(updatedUsers);
    localStorage.setItem("users", JSON.stringify(updatedUsers));
    setUser({ name: "", email: "", password: "", passwordconf: "" });
    navigate("/")
  };

  return (
    <div className="items-center p-6">
    <Navbar/>
    <div className="row">
      <h2 className="font-bold mb-4">Register</h2>
      <form onSubmit={handleSubmit} className=" w-80">
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={user.name}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={user.email}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={user.password}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="password"
          name="passwordconf"
          placeholder="Confirm Password"
          value={user.passwordconf}
          onChange={handleChange}
          className="border p-2 rounded"
        />

        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
          Register
        </button>
        
      </form>
      </div>
    </div>
  );
};

export default Register;
