// IntakeList.js
import React, { useState, useEffect } from 'react';

const IntakeList = () => {
  const [intakeData, setIntakeData] = useState({});
  const [pageNumber, setPageNumber] = useState(0);
  const user = JSON.parse(localStorage.getItem('loggedInUser'));

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem('intakeData'));
    if (data && data[user.username]) {
      setIntakeData(data[user.username]);
    }
  }, []);

  const pages = Math.ceil(Object.keys(intakeData).length / 10);
  const startIndex = pageNumber * 10;
  const endIndex = startIndex + 10;
  const paginatedData = Object.keys(intakeData).slice(startIndex, endIndex).map((date) => intakeData[date]);

  return (
    <div>
      <h2>Intake List</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Intake (ml)</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {paginatedData.map((data) => (
            <tr key={data.date}>
              <td>{data.date}</td>
              <td>{data.intake}</td>
              <td>{data.time}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        {pageNumber > 0 && (
          <button onClick={() => setPageNumber(pageNumber - 1)}>Previous</button>
        )}
        {pageNumber < pages - 1 && (
          <button onClick={() => setPageNumber(pageNumber + 1)}>Next</button>
        )}
      </div>
    </div>
  );
};

export default Lis;
